1.query:
timeseries usage=avg(dt.process.cpu.usage),
    by:{dt.entity.process_group_instance},
    filter:{
      dt.entity.process_group in [
        fetch dt.entity.process_group
        | filter in(belongs_to[dt.entity.cloud_application_namespace], "CLOUD_APPLICATION_NAMESPACE-908795E56153B60B")
        | fields id
      ]
    }
// | lookup [fetch dt.entity.process_group_instance | fields id, runs, belongs_to, mz=managementZones], sourceField:dt.entity.process_group_instance, lookupField:id
// | filter matchesValue(lookup.mz,"EasyTrade")
// //| lookup [fetch dt.entity.process_group_instance | fields id, runs, belongs_to, mz=managementZones], sourceField:dt.entity.process_group_instance, lookupField:id
// //| filter ("ALL" == $Service OR $Service ==  toString(belongs_to))
//| filter isNotNull(lookup.runs)
| lookup [fetch dt.entity.process_group_instance | fields id, serviceId=runs[dt.entity.service], belongs_to], sourceField:dt.entity.process_group_instance, lookupField:id
| filter isNotNull(lookup.serviceId)
| expand lookup.serviceId
| lookup [fetch dt.entity.service | fields id, entity.name], sourceField:lookup.serviceId, lookupField:id
| filter in($Service , "ALL") OR in(lookup.entity.name, $Service)
| sort arrayAvg(usage) desc
| limit 20
| lookup [fetch dt.entity.process_group_instance],sourceField:dt.entity.process_group_instance,lookupField:id,fields:{dt.entity.process_group_instance.name=entity.name}
| fields timeframe, interval, dt.entity.process_group_instance.name, usage
| sort usage desc

8.query:
fetch spans
| filter request.is_root_span == true
| filter k8s.namespace.name == "prod"
| filter isNotNull(endpoint.name)
| filter dt.internal.is_failed == true or request.is_failed == true
| lookup [ fetch dt.entity.service ], 
         lookupField:id, sourceField:dt.entity.service, 
         fields: {service = entity.name}
| filter in($Service , "ALL") OR in(service, $Service)
| fieldsAdd timestamp = start_time
//| fieldsAdd ResponseCode = toLong(http.response.status_code)
//| filter ResponseCode >= 500 and ResponseCode < 600 
| makeTimeSeries Errors = count(), by:{service}

45.query:
fetch spans
| filter request.is_root_span == true
| filter k8s.namespace.name == "prod"
| filter span.kind == "server"
| filterOut endpoint.name == "Images" or endpoint.name == "JavaScript" or endpoint.name == "CSS" 
| lookup [ fetch dt.entity.service ], 
         lookupField:id, sourceField:dt.entity.service, 
         fields: {service = entity.name }
| filter in($Service , "ALL") OR in(service, $Service)
| fieldsAdd exception.message = span.events[0][exception.message]
| fieldsAdd exception.class = span.events[0][exception.type]
| fieldsAdd eventname = span.events[0][span_event.name]
| filter eventname == "exception"
// | lookup [ fetch dt.entity.process_group_instance     
//          | fields name=entity.name, id=id],
//            sourceField:dt.entity.process_group_instance, lookupField:id, prefix:"lookup.pgi_"
//| summarize Count = count(), by:{Service = service, Process = lookup.pgi_name, Class = exception.class, Message = exception.message}
| summarize Count = count(), by:{Service = service, Request = endpoint.name, Class = exception.class, Message = exception.message}
| sort Count desc
//| fields Service, Process, Class, Message, Count
//| fields Service = replaceString(Service, "[eks][prod] hipstershop.","" ), Message, Count
| fields Service, Request, Message, Count
| limit 10
