import { queryExecutionClient } from '@dynatrace-sdk/client-query';

export default async function () {

const From=$dt_timeframe_from;
const To=$dt_timeframe_to;
const Status = $Status;
const MuteStatus = $MuteStatus;
const StackLevel = $StackLevel.join('","');
const RiskLevel = $RiskLevel.join('","');

async function pollQuery(requestToken, resolve) {

  const poll = await queryExecutionClient.queryPoll({requestToken, requestTimeoutMilliseconds: 3000});
  if(poll.result) {
      resolve(poll.result);
  } else {
      pollQuery(requestToken, resolve);
  }
}
function runQuery (query) {
  return new Promise(async (resolve, reject) => {

      const queryExecution = await queryExecutionClient.queryExecute({body: { query }});
      if(queryExecution.result) {
          resolve(queryExecution.result);
      } else {
          pollQuery(queryExecution.requestToken, resolve)
      }

  });
}

 const commonFilterQueryPart = `fetch events, from:toTimestamp("${From}"), to:toTimestamp("${To}")
| filter dt.system.bucket=="default_security_events" // only security events
| filter event.provider=="Dynatrace" // only events generated by dynatrace
| filter event.type=="VULNERABILITY_STATE_REPORT_EVENT" // historical vulnerability data
| filter event.level=="ENTITY" // on entity level
// filter only the latest snapshot data
| dedup {vulnerability.id,affected_entity.id}, sort: { timestamp desc }
// end of filtering for the latest snapshot
| filter vulnerability.parent.resolution.status == "${Status}" OR "All" == "${Status}"
| filter vulnerability.parent.mute.status == "${MuteStatus}" OR "All" == "${MuteStatus}"
| filter in(vulnerability.stack,"${StackLevel}")
| filter in(vulnerability.parent.risk.level,"${RiskLevel}")
| filter vulnerability.resolution.status == "OPEN"
| filter vulnerability.mute.status == "NOT_MUTED"`;


 const vulnBySeverityQueryString = `${commonFilterQueryPart}
 | summarize
{
Critical=countIf(vulnerability.risk.level=="CRITICAL"),
High=countIf(vulnerability.risk.level=="HIGH"),
Medium=countIf(vulnerability.risk.level=="MEDIUM"),
Low=countIf(vulnerability.risk.level=="LOW"),
maxScore=takeMax(vulnerability.risk.score)
}, by: {vulnerability.display_id}
| summarize {
C=countIf(Critical>0),
H=countIf(High>0),
M=countIf(Medium>0),
L=countIf(Low>0)
}`;

 const avgRiskScoreQueryString = `${commonFilterQueryPart}
 | sort timestamp, direction:"descending"
 | summarize {
 average=round(avg(toDouble(vulnerability.risk.score)),decimals:1)
 }, by:{risk=vulnerability.risk.level}`;


  // Get the counts per risk level using DQL
  console.log(vulnBySeverityQueryString);
  const vulnBySeverityQueryResult = await runQuery(vulnBySeverityQueryString);

  if (vulnBySeverityQueryResult.records == null || vulnBySeverityQueryResult.records.length==0){
    return "No Data";
  }
  const vulnBySeverity=vulnBySeverityQueryResult.records[0];

    // Define data set: Number of problems per criticality level

    // Get the avg score per risk level using DQL
  const avgRiskScoreQueryResult = await runQuery(avgRiskScoreQueryString);

  const avgRiskScore=avgRiskScoreQueryResult.records.reduce((obj,record)=>{

    if(record == null ||  obj == null || record.average == null) return;
    if(record.risk=="CRITICAL"){
      obj.C=record.average;
    }
    if(record.risk=="HIGH"){
      obj.H=record.average;
    }
    if(record.risk=="MEDIUM"){
      obj.M=record.average;
    }
    if(record.risk=="LOW"){
      obj.L=record.average;
    }
    return obj;
  },{"C":0,"H":0,"M":0,"L":0});

  // Define weights of the criticality level. Higher weight means that the contribution in the highest criticality level is higher.
  const weights = {
    "C": 60,
    "H": 20,
    "M": 5,
    "L": 1,
  };

  // Define ranges for the criticality levels
  const scoreRanges = {
      "C": [9, 10],
      "H": [7, 9],
      "M": [4, 7],
      "L": [0.5, 4],
    };


  // Normalize the average risk score of each severity against the score range of that severity (output between 0 and 1).
  const normalizedAvgSeverities={}
  for(const[severity, avg] of Object.entries(avgRiskScore)){
    const minScore=scoreRanges[severity][0];
    const maxScore=scoreRanges[severity][1];
    const scoreRange=maxScore-minScore;
    normalizedAvgSeverities[severity]=(avgRiskScore[severity]-minScore)/scoreRange;
  }
    // Define logistic function and scale to make sense of the amount of problems in a criticality level (output is always between 0 and 1)
    const scale=10
    function logistic(x) {
      return Math.tanh(x/scale)
    }

    const severities = Object.keys(vulnBySeverity);

    let score=0;
    let minScore=0;
    let maxScore=0;

    const unrelevantSeverities=[];

    // Calculate min/max and the distance between min and max (the final score range) in the highest available criticality level
    for (const severity of severities) {
      if (vulnBySeverity[severity] >= 1) {
        minScore=scoreRanges[severity][0];
        maxScore=scoreRanges[severity][1];
        break;
      }else{
        // Remove all severities that are above the maximum range to have a better result for lower scores
        unrelevantSeverities.push(severity);
      }
    }
    const scoreRange=maxScore-minScore;

    const relevantWeights=Object.fromEntries(Object.entries(weights).filter(([severity,weight]) => !unrelevantSeverities.includes(severity)));
    const relevantSeverities=severities.filter((severity,weight) => !unrelevantSeverities.includes(severity));


    // Convert weights into percentage based ratios
    const weightRatios={};
    let totalWeight = Object.values(relevantWeights).reduce((acc, number) => acc + number, 0);
    for(const[severity, weight] of Object.entries(relevantWeights)){
      weightRatios[severity]=weight/totalWeight;
    }

    // Increase the min score of the highest criticality level by the score contribution of each criticality level (based on weight and count)
    let addedScore=0;
    for(const severity of relevantSeverities){
      const maxAddedScore=weightRatios[severity]*scoreRange;
      const scoreContribution=maxAddedScore*logistic(vulnBySeverity[severity])*normalizedAvgSeverities[severity];
      addedScore+=scoreContribution
    }

    // Calculate final score
    const finalScore= (minScore + addedScore)*10;
    let scoreStr=finalScore.toPrecision(2);
    return scoreStr;
}

