variables.input:
timeseries t_free=avg(`com.dynatrace.extension.snmp-generic-cisco-device.cpm.cpu.memory.hc.free`),t_used=avg(`com.dynatrace.extension.snmp-generic-cisco-device.cpm.cpu.memory.hc.used`),
by: {`dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device`}
| lookup [fetch `dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device` | fields id, entity.name, system_name, lifetime[end] ],
    sourceField:`dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device`,
    lookupField:id,
    fields:{entity.name,system_name,`lifetime[end]` }
| fieldsadd `Used Memory (%)` = 100*(t_used[]/(t_free[]+ t_used[]))
//| fieldsRename `DNS name`=system_name
//| fieldsRemove entity.name, `dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device`, t_free, t_used
//| sort `Used Memory (%)` desc
| fields system_name

48.query:
fetch `dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_network_interface`
| fieldsAdd   opStatus, adminStatus
| filter opStatus == "down(2)" and adminStatus == "up(1)"
| summarize sum_int=count()
//|append [fetch `dt.entity.snmp:com_dynatrace_extension_snmp_generic_juniper_network_interface`
//| fieldsAdd   if_adminstatus,if_operstatus
//| filter if_operstatus == "down(2)" and if_adminstatus == "down(2)"
//| summarize sum_int=count()]

//|append [fetch `dt.entity.snmp:com_dynatrace_extension_snmp_generic_device`
//| fieldsAdd   opStatus, adminStatus
//| filter opStatus == "down(2)" and adminStatus == "up(1)"
//| summarize sum_int=count()]
|append [fetch `dt.entity.palo-alto:interface`
| fieldsAdd   InterfaceAdminStatus,IFOperatingStatus
| filter IFOperatingStatus == "down(2)" and InterfaceAdminStatus == "down(2)"
| summarize sum_int=count()]
|append [fetch `dt.entity.f5:interface`
| fieldsAdd   EnabledState,Status
| filter Status == "down(1)" and EnabledState == "true(1)"
| summarize sum_int=count()]
| summarize sum_down=sum(sum_int)

54.query:
timeseries s11=avg(custom.dynatrace.cisco.temperature.s11), by: { sys.name }
| sort arrayAvg(s11) desc
| fieldsAdd sensor11=((s11[]*9)/5)+32
//| fields sensor11, timestamp
| append [timeseries s6=avg(custom.dynatrace.cisco.temperature.s6), by: { sys.name }
  | sort arrayAvg(s6) desc
  | fieldsAdd sensor6=((s6[]*9)/5)+32
 // | fields sensor6
  ]
| append [timeseries s12=avg(custom.dynatrace.cisco.temperature.s12), by: { sys.name }
  | sort arrayAvg(s12) desc
  | fieldsAdd sensor12=((s12[]*9)/5)+32
 // | fields sensor12
  ]
| append [timeseries s24=avg(custom.dynatrace.cisco.temperature.s24), by: { sys.name }
  | sort arrayAvg(s24) desc
  | fieldsAdd sensor24=((s24[]*9)/5)+32
  //| fields sensor24
  ]
| append [timeseries s7001=avg(custom.dynatrace.cisco.temperature.s7001), by: { sys.name }
  | sort arrayAvg(s7001) desc
  | fieldsAdd sensor7001=((s7001[]*9)/5)+32
  //| fields sensor7001
  ]
| append [timeseries ootb=avg(`com.dynatrace.extension.snmp-generic-cisco-device.envmon.temperature.status.value`), by: { sys.name }
  | sort arrayAvg(ootb) desc
  | fieldsAdd sensorOOTB=((ootb[]*9)/5)+32
  //| fields sensor7001
  ]
  | fieldsRemove s11, s6, s12, s24, s7001, ootb
  | filter in(sys.name, $Device)
