* For information visit https://dt-url.net/functions-help
*/
import { queryExecutionClient } from '@dynatrace-sdk/client-query';

export default async function () {
  const timeout = 60;
  const query = 'fetch logs | filter contains(log.source, "/disk/docker/containers/") | filter matchesValue(status,"ERROR") | fields dt.entity.container_group_instance';
  const response = await queryExecutionClient.queryExecute({ body: { query, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout  } });
  const jsonRecord = response["result"]["records"][0]["dt.entity.container_group_instance"].toString();
  const jsonPrefix = "[Container Details](https://fcr78461.apps.dynatrace.com/ui/apps/dynatrace.classic.containers/ui/entity/";
  const jsonMarkdown = jsonPrefix.concat(jsonRecord);
  const jsonFinalMarkdown = jsonMarkdown.concat(")");
  return jsonFinalMarkdown;
}

